		Строка1 = РезультатТаблица[0];

		Если Строка1.Склад <> ОжидаемыйСклад Тогда
			Возврат Ложь;
		КонецЕсли;

		Если Строка1.Товар <> Товар Тогда
			Возврат Ложь;
		КонецЕсли;

	КонецЕсли;

	Возврат Истина;

